import React ,{Component} from 'react';
import {
  StyleSheet,
  View,
  Animated,
  Dimensions,
  Text,
  Image,
} from 'react-native';

export const DURATION = {
  LENGTH_LONG: 2000,
  LENGTH_SHORT: 500,
  FOREVER: 0,
};

const {height, width} = Dimensions.get('window');

export default class Toast extends Component{

  constructor(props){
    super(props);

    this.state = {
      isShow:false,
      text:'',
      opacityValue: new Animated.Value(0),
      type: '', //success, fail, loading, ''
      position: 'center',// 'center','top','bottom' 暂时保留，后续可扩展
    }
  }

  show(text, duration, type){
    this.duration = typeof duration === 'number' ? duration : DURATION.LENGTH_LONG;

    this.setState({
      isShow: true,
      text: text,
      type: type,
    });

    Animated.timing(
          this.state.opacityValue,
          {
            toValue: this.props.opacity,
            duration: this.props.fadeInDuration,
          }
      ).start(()=>{
        this.isShow = true;//  setState??????
        if(duration !== DURATION.FOREVER){
          this.close();
        }
      });
  }

  close(duration){
    let delay = typeof duration ==='undefined' ? this.duration : duration;

    if(delay === DURATION.FOREVER){
      delay = this.props.defaultCloseDelay || 250;
    }
    if(!this.isShow && !this.state.isShow) return;
    this.timer && clearTimeout(this.timer);
    this.timer = setTimeout(() => {
      Animated.timing(
        this.state.opacityValue,
        {
          toValue: 0.0,
          duration: this.props.fadeOutDuration,
        }
      ).start(() => {
        this.setState({
          isShow: false,
        });
        this.isShow = false;
      });
    }, delay);
  }

  componentWillUnmount(){
    this.timer && clearTimeout(this.timer);
  }

  render(){
    let pos, imageSource;
    switch(this.props.postion){ // 
      case 'top':
        pos = this.props.positionValue;
        break;
      case 'center':
        pos = height/2;
        break;
      case 'bottom':
        pos = height - this.props.positionValue;
        break;
      default:
        break;
    }
    switch(this.state.type){
      case 'success':
        imageSource = iconImage.success;
        break;
      case 'loading':
        imageSource = iconImage.loading;
        break;
      case 'fail':
        imageSource = iconImage.fail;
        break;
      default:
        break;
    }

    const view = this.state.isShow ? 
      <View
          style={[styles.container, {top: pos}]}
          pointerEvents="none"
      >
        <Animated.View
          style={[styles.content, {opacity: this.state.opacityValue}, this.props.style]} 
        >
          {(this.state.type !== '') && <Image style={styles.iconImage} source={{uri:imageSource}} /> }
          <Text style={this.props.textStyle}>{this.state.text}</Text>
        </Animated.View>
      </View> : null;
      return view;
  }
}

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    left: 0,
    right: 0,
    alignItems: 'center',
  },
  content: {
    backgroundColor: 'black',
    borderRadius: 5,
    padding: 10,
    flex: 1,
    flexDirection: 'row',
    justifyContent:'center',
    alignItems:'center',
  },
  text: {
    color: 'white',
  },
  iconImage:{
    width:10,
    height:10,
    marginLeft:5,
    marginRight:10,
  }
});


Toast.propTypes = {
  style: View.propTypes.style,
  position: React.PropTypes.oneOf([
    'top',
    'center',
    'bottom',
  ]),
  textStyle: Text.propTypes.style,
  positionValue: React.PropTypes.number,
  fadeInDuration: React.PropTypes.number,
  fadeOutDuration: React.PropTypes.number,
  opacity: React.PropTypes.number,
}

Toast.defaultProps = {
  position: 'center',
  textStyle: styles.text,
  positionValue: 120,
  fadeInDuration: 500,
  fadeOutDuration: 500,
  opacity: 1,
}

const iconImage = {
  success:'data:image/png;base64,',
  fail:'data:image/png;base64,',
  loading:'data:image/png;base64,',
}






